WITH date_period AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy BETWEEN 1 AND 4), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_county IN ('Rush County', 'Toole County', 'Jefferson County', 'Dona Ana County', 'La Porte County')), store_sales_customers AS (SELECT DISTINCT ss_customer_sk FROM store_sales JOIN date_period ON ss_sold_date_sk = d_date_sk), web_sales_customers AS (SELECT DISTINCT ws_bill_customer_sk FROM web_sales JOIN date_period ON ws_sold_date_sk = d_date_sk), catalog_sales_customers AS (SELECT DISTINCT cs_ship_customer_sk FROM catalog_sales JOIN date_period ON cs_sold_date_sk = d_date_sk), any_sales_customers AS (SELECT ws_bill_customer_sk AS customer_sk FROM web_sales_customers UNION ALL SELECT cs_ship_customer_sk FROM catalog_sales_customers), customers_with_sales AS (SELECT DISTINCT c.c_customer_sk, c.c_current_cdemo_sk, c.c_current_addr_sk FROM customer AS c JOIN store_sales_customers AS ss ON c.c_customer_sk = ss.ss_customer_sk JOIN any_sales_customers AS asc ON c.c_customer_sk = asc.customer_sk)
SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3, cd_dep_count, COUNT(*) AS cnt4, cd_dep_employed_count, COUNT(*) AS cnt5, cd_dep_college_count, COUNT(*) AS cnt6 FROM customers_with_sales AS cws JOIN filtered_customer_address AS fca ON cws.c_current_addr_sk = fca.ca_address_sk JOIN customer_demographics AS cd ON cws.c_current_cdemo_sk = cd.cd_demo_sk GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating, cd_dep_count, cd_dep_employed_count, cd_dep_college_count LIMIT 100