WITH filtered_d1 AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name = '2001Q1'), filtered_d2 AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), filtered_d3 AS (SELECT d_date_sk FROM date_dim WHERE d_quarter_name IN ('2001Q1', '2001Q2', '2001Q3')), filtered_store_sales AS (SELECT ss_item_sk, ss_customer_sk, ss_ticket_number, ss_store_sk, ss_quantity FROM store_sales JOIN filtered_d1 ON ss_sold_date_sk = d_date_sk), filtered_store_returns AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, sr_return_quantity FROM store_returns JOIN filtered_d2 ON sr_returned_date_sk = d_date_sk), filtered_catalog_sales AS (SELECT cs_item_sk, cs_bill_customer_sk, cs_quantity FROM catalog_sales JOIN filtered_d3 ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, i_item_desc, s_state, COUNT(ss.ss_quantity) AS store_sales_quantitycount, AVG(ss.ss_quantity) AS store_sales_quantityave, STDDEV_SAMP(ss.ss_quantity) AS store_sales_quantitystdev, STDDEV_SAMP(ss.ss_quantity) / AVG(ss.ss_quantity) AS store_sales_quantitycov, COUNT(sr.sr_return_quantity) AS store_returns_quantitycount, AVG(sr.sr_return_quantity) AS store_returns_quantityave, STDDEV_SAMP(sr.sr_return_quantity) AS store_returns_quantitystdev, STDDEV_SAMP(sr.sr_return_quantity) / AVG(sr.sr_return_quantity) AS store_returns_quantitycov, COUNT(cs.cs_quantity) AS catalog_sales_quantitycount, AVG(cs.cs_quantity) AS catalog_sales_quantityave, STDDEV_SAMP(cs.cs_quantity) AS catalog_sales_quantitystdev, STDDEV_SAMP(cs.cs_quantity) / AVG(cs.cs_quantity) AS catalog_sales_quantitycov FROM filtered_store_sales AS ss JOIN filtered_store_returns AS sr ON (ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk AND ss.ss_ticket_number = sr.sr_ticket_number) JOIN filtered_catalog_sales AS cs ON (sr.sr_customer_sk = cs.cs_bill_customer_sk AND sr.sr_item_sk = cs.cs_item_sk) JOIN store ON ss.ss_store_sk = s_store_sk JOIN item ON ss.ss_item_sk = i_item_sk GROUP BY i_item_id, i_item_desc, s_state ORDER BY i_item_id, i_item_desc, s_state LIMIT 100