WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 11 AND d_year = 1998), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_manager_id = 8), filtered_store AS (SELECT s_store_sk, SUBSTRING(s_zip, 1, 5) AS s_zip_prefix FROM store), filtered_customer AS (SELECT c_customer_sk, c_current_addr_sk FROM customer), filtered_customer_address AS (SELECT ca_address_sk, SUBSTRING(ca_zip, 1, 5) AS ca_zip_prefix FROM customer_address), qualified_sales AS (SELECT ss.ss_ext_sales_price, i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact FROM store_sales AS ss JOIN filtered_date AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_item AS i ON ss.ss_item_sk = i.i_item_sk)
SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM qualified_sales AS qs JOIN filtered_customer AS c ON qs.ss_customer_sk = c.c_customer_sk JOIN filtered_customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_store AS s ON qs.ss_store_sk = s.s_store_sk AND ca.ca_zip_prefix <> s.s_zip_prefix GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100