WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 11 AND d_year = 1998), filtered_item AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_manager_id = 8), filtered_store AS (SELECT s_store_sk, s_zip FROM store), filtered_customer AS (SELECT c_customer_sk, c_current_addr_sk FROM customer), filtered_address AS (SELECT ca_address_sk, ca_zip FROM customer_address), early_join AS (SELECT ss.ss_item_sk, ss.ss_customer_sk, ss.ss_store_sk, ss.ss_ext_sales_price, i.i_brand_id, i.i_brand, i.i_manufact_id, i.i_manufact, ca.ca_zip, s.s_zip FROM store_sales AS ss JOIN filtered_date AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_item AS i ON ss.ss_item_sk = i.i_item_sk JOIN filtered_customer AS c ON ss.ss_customer_sk = c.c_customer_sk JOIN filtered_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk)
SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM early_join WHERE SUBSTRING(ca_zip, 1, 5) <> SUBSTRING(s_zip, 1, 5) GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100