WITH date_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), customer_filtered AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College'), promo_email AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'N'), promo_event AS (SELECT p_promo_sk FROM promotion WHERE p_channel_event = 'N')
SELECT i_item_id, AVG(cs_quantity) AS agg1, AVG(cs_list_price) AS agg2, AVG(cs_coupon_amt) AS agg3, AVG(cs_sales_price) AS agg4 FROM ((SELECT cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_item_sk FROM catalog_sales JOIN date_filtered ON cs_sold_date_sk = d_date_sk JOIN customer_filtered ON cs_bill_cdemo_sk = cd_demo_sk JOIN promo_email ON cs_promo_sk = p_promo_sk) UNION ALL (SELECT cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_item_sk FROM catalog_sales JOIN date_filtered ON cs_sold_date_sk = d_date_sk JOIN customer_filtered ON cs_bill_cdemo_sk = cd_demo_sk JOIN promo_event ON cs_promo_sk = p_promo_sk)) AS sales JOIN item ON cs_item_sk = i_item_sk GROUP BY i_item_id ORDER BY i_item_id LIMIT 100