WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), filtered_items AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 977), catalog_sales_with_dates AS (SELECT cs_item_sk, cs_ext_discount_amt FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = filtered_dates.d_date_sk), item_avg_discount AS (SELECT cs_item_sk, 1.3 * AVG(cs_ext_discount_amt) AS threshold_avg FROM catalog_sales_with_dates GROUP BY cs_item_sk)
SELECT SUM(cs.cs_ext_discount_amt) AS "excess discount amount" FROM catalog_sales_with_dates AS cs JOIN filtered_items AS i ON cs.cs_item_sk = i.i_item_sk JOIN item_avg_discount AS avgd ON cs.cs_item_sk = avgd.cs_item_sk WHERE cs.cs_ext_discount_amt > avgd.threshold_avg LIMIT 100