WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 12), web_agg AS (SELECT ws.ws_item_sk AS item, ws.ws_order_number, ws.ws_item_sk, ws.ws_quantity, ws.ws_net_paid, wr.wr_return_quantity, wr.wr_return_amt FROM web_sales AS ws LEFT OUTER JOIN web_returns AS wr ON (ws.ws_order_number = wr.wr_order_number AND ws.ws_item_sk = wr.wr_item_sk AND wr.wr_return_amt > 10000), filtered_dates WHERE ws.ws_net_profit > 1 AND ws.ws_net_paid > 0 AND ws.ws_quantity > 0 AND ws.ws_sold_date_sk = filtered_dates.d_date_sk), catalog_agg AS (SELECT cs.cs_item_sk AS item, cs.cs_order_number, cs.cs_item_sk, cs.cs_quantity, cs.cs_net_paid, cr.cr_return_quantity, cr.cr_return_amount FROM catalog_sales AS cs LEFT OUTER JOIN catalog_returns AS cr ON (cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk AND cr.cr_return_amount > 10000), filtered_dates WHERE cs.cs_net_profit > 1 AND cs.cs_net_paid > 0 AND cs.cs_quantity > 0 AND cs.cs_sold_date_sk = filtered_dates.d_date_sk), store_agg AS (SELECT sts.ss_item_sk AS item, sts.ss_ticket_number, sts.ss_item_sk, sts.ss_quantity, sts.ss_net_paid, sr.sr_return_quantity, sr.sr_return_amt FROM store_sales AS sts LEFT OUTER JOIN store_returns AS sr ON (sts.ss_ticket_number = sr.sr_ticket_number AND sts.ss_item_sk = sr.sr_item_sk AND sr.sr_return_amt > 10000), filtered_dates WHERE sts.ss_net_profit > 1 AND sts.ss_net_paid > 0 AND sts.ss_quantity > 0 AND sts.ss_sold_date_sk = filtered_dates.d_date_sk)
SELECT channel, item, return_ratio, return_rank, currency_rank FROM (SELECT 'web' AS channel, web.item, web.return_ratio, web.return_rank, web.currency_rank FROM (SELECT item, return_ratio, currency_ratio, RANK() OVER (ORDER BY return_ratio) AS return_rank, RANK() OVER (ORDER BY currency_ratio) AS currency_rank FROM (SELECT item, (CAST(SUM(COALESCE(wr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ws_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(wr_return_amt, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ws_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM web_agg GROUP BY item) AS in_web) AS web WHERE (web.return_rank <= 10 OR web.currency_rank <= 10) UNION SELECT 'catalog' AS channel, catalog.item, catalog.return_ratio, catalog.return_rank, catalog.currency_rank FROM (SELECT item, return_ratio, currency_ratio, RANK() OVER (ORDER BY return_ratio) AS return_rank, RANK() OVER (ORDER BY currency_ratio) AS currency_rank FROM (SELECT item, (CAST(SUM(COALESCE(cr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(cs_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(cr_return_amount, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(cs_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM catalog_agg GROUP BY item) AS in_cat) AS catalog WHERE (catalog.return_rank <= 10 OR catalog.currency_rank <= 10) UNION SELECT 'store' AS channel, store.item, store.return_ratio, store.return_rank, store.currency_rank FROM (SELECT item, return_ratio, currency_ratio, RANK() OVER (ORDER BY return_ratio) AS return_rank, RANK() OVER (ORDER BY currency_ratio) AS currency_rank FROM (SELECT item, (CAST(SUM(COALESCE(sr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ss_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(sr_return_amt, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ss_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM store_agg GROUP BY item) AS in_store) AS store WHERE (store.return_rank <= 10 OR store.currency_rank <= 10)) AS sq1 ORDER BY 1 NULLS FIRST, 4 NULLS FIRST, 5 NULLS FIRST, 2 NULLS FIRST LIMIT 100