WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 2), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_items AS (SELECT i_item_sk, i_item_id FROM item WHERE i_color IN ('slate', 'blanched', 'burnished')), filtered_store_sales AS (SELECT ss_item_sk, ss_ext_sales_price FROM store_sales JOIN filtered_dates ON store_sales.ss_sold_date_sk = filtered_dates.d_date_sk JOIN filtered_addresses ON store_sales.ss_addr_sk = filtered_addresses.ca_address_sk), filtered_web_sales AS (SELECT ws_item_sk, ws_ext_sales_price FROM web_sales JOIN filtered_dates ON web_sales.ws_sold_date_sk = filtered_dates.d_date_sk JOIN filtered_addresses ON web_sales.ws_bill_addr_sk = filtered_addresses.ca_address_sk), ss AS (SELECT filtered_items.i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM filtered_store_sales JOIN filtered_items ON filtered_store_sales.ss_item_sk = filtered_items.i_item_sk GROUP BY filtered_items.i_item_id), ws AS (SELECT filtered_items.i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM filtered_web_sales JOIN filtered_items ON filtered_web_sales.ws_item_sk = filtered_items.i_item_sk GROUP BY filtered_items.i_item_id), cs AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales, date_dim, customer_address, item WHERE i_item_id IN (SELECT i_item_id FROM item WHERE i_color IN ('slate', 'blanched', 'burnished')) AND cs_item_sk = i_item_sk AND cs_sold_date_sk = d_date_sk AND d_year = 2001 AND d_moy = 2 AND cs_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -5 GROUP BY i_item_id)
SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY total_sales NULLS FIRST, i_item_id NULLS FIRST LIMIT 100