WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_stores AS (SELECT s_store_sk FROM store WHERE s_gmt_offset = -5), filtered_items AS (SELECT i_item_sk FROM item WHERE i_category = 'Jewelry'), base_sales AS (SELECT ss_ext_sales_price, ss_promo_sk FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_stores ON ss_store_sk = s_store_sk JOIN filtered_items ON ss_item_sk = i_item_sk), promotional_sales AS (SELECT SUM(ss_ext_sales_price) AS promotions FROM base_sales AS bs JOIN customer AS c ON bs.ss_customer_sk = c.c_customer_sk JOIN filtered_addresses AS ca ON ca.ca_address_sk = c.c_current_addr_sk JOIN promotion AS p ON bs.ss_promo_sk = p.p_promo_sk WHERE p_channel_dmail = 'Y' OR p_channel_email = 'Y' OR p_channel_tv = 'Y'), all_sales AS (SELECT SUM(ss_ext_sales_price) AS total FROM base_sales AS bs JOIN customer AS c ON bs.ss_customer_sk = c.c_customer_sk JOIN filtered_addresses AS ca ON ca.ca_address_sk = c.c_current_addr_sk)
SELECT promotions, total, CAST(promotions AS DECIMAL(15, 4)) / CAST(total AS DECIMAL(15, 4)) * 100 FROM promotional_sales, all_sales ORDER BY promotions, total LIMIT 100