WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), filtered_stores AS (SELECT s_store_sk, s_gmt_offset FROM store WHERE s_gmt_offset = -5), filtered_addresses AS (SELECT ca_address_sk, ca_gmt_offset FROM customer_address WHERE ca_gmt_offset = -5), filtered_items AS (SELECT i_item_sk FROM item WHERE i_category = 'Jewelry'), common_sales AS (SELECT ss_ext_sales_price, ss_promo_sk, ss_store_sk, ss_customer_sk, ss_item_sk FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_stores ON ss_store_sk = s_store_sk JOIN filtered_addresses ON ss_customer_sk IN (SELECT c_customer_sk FROM customer WHERE c_current_addr_sk = ca_address_sk) JOIN filtered_items ON ss_item_sk = i_item_sk), promotional_sales AS (SELECT SUM(ss_ext_sales_price) AS promotions FROM common_sales AS cs JOIN promotion AS p ON cs.ss_promo_sk = p.p_promo_sk WHERE p_channel_dmail = 'Y' OR p_channel_email = 'Y' OR p_channel_tv = 'Y'), all_sales AS (SELECT SUM(ss_ext_sales_price) AS total FROM common_sales)
SELECT promotions, total, CAST(promotions AS DECIMAL(15, 4)) / CAST(total AS DECIMAL(15, 4)) * 100 FROM promotional_sales, all_sales ORDER BY promotions, total LIMIT 100