WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), filtered_cdemo AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College'), filtered_promo_email AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'N'), filtered_promo_event AS (SELECT p_promo_sk FROM promotion WHERE p_channel_event = 'N')
SELECT i.i_item_id, AVG(ss.ss_quantity) AS agg1, AVG(ss.ss_list_price) AS agg2, AVG(ss.ss_coupon_amt) AS agg3, AVG(ss.ss_sales_price) AS agg4 FROM store_sales AS ss JOIN filtered_date AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_cdemo AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN item AS i ON ss.ss_item_sk = i.i_item_sk WHERE (ss.ss_promo_sk IN (SELECT p_promo_sk FROM filtered_promo_email) OR ss.ss_promo_sk IN (SELECT p_promo_sk FROM filtered_promo_event)) GROUP BY i.i_item_id ORDER BY i.i_item_id LIMIT 100