WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College'), sales_branch1 AS (SELECT ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price, i_item_id FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_customer_demographics ON ss_cdemo_sk = cd_demo_sk JOIN item ON ss_item_sk = i_item_sk JOIN promotion ON ss_promo_sk = p_promo_sk WHERE p_channel_email = 'N'), sales_branch2 AS (SELECT ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price, i_item_id FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_customer_demographics ON ss_cdemo_sk = cd_demo_sk JOIN item ON ss_item_sk = i_item_sk JOIN promotion ON ss_promo_sk = p_promo_sk WHERE p_channel_event = 'N' AND (p_channel_email <> 'N' OR p_channel_email IS NULL))
SELECT i_item_id, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM (SELECT * FROM sales_branch1 UNION ALL SELECT * FROM sales_branch2) AS unioned_sales GROUP BY i_item_id ORDER BY i_item_id LIMIT 100