WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_dow = 1 AND d_year IN (1999, 2000, 2001)), filtered_store AS (SELECT s_store_sk, s_city FROM store WHERE s_number_employees BETWEEN 200 AND 295), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count = 6 OR hd_vehicle_count > 2), store_sales_summary AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_store AS fs ON ss.ss_store_sk = fs.s_store_sk JOIN filtered_hd AS fhd ON ss.ss_hdemo_sk = fhd.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city)
SELECT c_last_name, c_first_name, SUBSTRING(s_city, 1, 30), ss_ticket_number, amt, profit FROM store_sales_summary AS ms JOIN customer ON ms.ss_customer_sk = c_customer_sk ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, SUBSTRING(s_city, 1, 30) NULLS FIRST, profit NULLS FIRST, ss_ticket_number LIMIT 100