WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_dow = 1 AND d_year IN (1999, 2000, 2001)), filtered_store AS (SELECT s_store_sk, s_city FROM store WHERE s_number_employees BETWEEN 200 AND 295), filtered_hd_dep AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count = 6), filtered_hd_vehicle AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_vehicle_count > 2), sales_branch1 AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store AS fs ON ss_store_sk = fs.s_store_sk JOIN filtered_hd_dep AS hd ON ss_hdemo_sk = hd.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city), sales_branch2 AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store AS fs ON ss_store_sk = fs.s_store_sk JOIN filtered_hd_vehicle AS hd ON ss_hdemo_sk = hd.hd_demo_sk LEFT JOIN filtered_hd_dep AS hd2 ON ss_hdemo_sk = hd2.hd_demo_sk WHERE hd2.hd_demo_sk IS NULL GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, fs.s_city), combined_sales AS (SELECT * FROM sales_branch1 UNION ALL SELECT * FROM sales_branch2)
SELECT c_last_name, c_first_name, SUBSTRING(s_city, 1, 30), ss_ticket_number, amt, profit FROM combined_sales JOIN customer ON ss_customer_sk = c_customer_sk ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, SUBSTRING(s_city, 1, 30) NULLS FIRST, profit NULLS FIRST, ss_ticket_number LIMIT 100