WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_dow = 1 AND d_year IN (1999, 2000, 2001)), filtered_stores AS (SELECT s_store_sk, s_city, s_number_employees FROM store WHERE s_number_employees BETWEEN 200 AND 295), branch1_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_stores ON ss_store_sk = s_store_sk JOIN household_demographics ON ss_hdemo_sk = hd_demo_sk WHERE hd_dep_count = 6 GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city), branch2_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_stores ON ss_store_sk = s_store_sk JOIN household_demographics ON ss_hdemo_sk = hd_demo_sk WHERE hd_vehicle_count > 2 GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city)
SELECT c_last_name, c_first_name, SUBSTRING(s_city, 1, 30), ss_ticket_number, amt, profit FROM (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city, amt, profit FROM branch1_sales UNION ALL SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, s_city, amt, profit FROM branch2_sales) AS ms JOIN customer ON ss_customer_sk = c_customer_sk ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, SUBSTRING(s_city, 1, 30) NULLS FIRST, profit NULLS FIRST, ss_ticket_number LIMIT 100