WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 62 AND 92 AND i_manufact_id IN (129, 270, 821, 423)), filtered_inventory AS (SELECT inv_item_sk FROM inventory JOIN filtered_dates ON d_date_sk = inv_date_sk WHERE inv_quantity_on_hand BETWEEN 100 AND 500)
SELECT i_item_id, i_item_desc, i_current_price FROM filtered_items AS fi JOIN filtered_inventory AS inv ON fi.i_item_sk = inv.inv_item_sk JOIN store_sales AS ss ON fi.i_item_sk = ss.ss_item_sk GROUP BY i_item_id, i_item_desc, i_current_price ORDER BY i_item_id LIMIT 100