WITH filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Edgewood'), filtered_income_band AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 38128 AND ib_upper_bound <= 38128 + 50000), filtered_household_demographics AS (SELECT hd_demo_sk FROM household_demographics AS hd JOIN filtered_income_band AS fib ON hd.hd_income_band_sk = fib.ib_income_band_sk), filtered_customer AS (SELECT c_customer_sk, c_current_cdemo_sk, c_current_hdemo_sk, c_customer_id, c_last_name, c_first_name FROM customer AS c JOIN filtered_customer_address AS fca ON c.c_current_addr_sk = fca.ca_address_sk JOIN filtered_household_demographics AS fhd ON c.c_current_hdemo_sk = fhd.hd_demo_sk), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics AS cd JOIN filtered_customer AS fc ON cd.cd_demo_sk = fc.c_current_cdemo_sk)
SELECT fc.c_customer_id AS customer_id, CONCAT(CONCAT(COALESCE(fc.c_last_name, ''), ', '), COALESCE(fc.c_first_name, '')) AS customername FROM filtered_customer AS fc JOIN store_returns AS sr ON EXISTS(SELECT 1 FROM filtered_customer_demographics AS fcd WHERE fcd.cd_demo_sk = sr.sr_cdemo_sk) ORDER BY fc.c_customer_id NULLS FIRST LIMIT 100