WITH filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Edgewood'), filtered_income AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 38128 AND ib_upper_bound <= 38128 + 50000), filtered_household AS (SELECT hd_demo_sk FROM household_demographics AS hd JOIN filtered_income AS ib ON hd.hd_income_band_sk = ib.ib_income_band_sk), filtered_customer AS (SELECT c_customer_id, c_last_name, c_first_name, c_current_cdemo_sk FROM customer AS c JOIN filtered_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_household AS hh ON c.c_current_hdemo_sk = hh.hd_demo_sk), filtered_demographics AS (SELECT cd_demo_sk FROM customer_demographics AS cd JOIN filtered_customer AS fc ON cd.cd_demo_sk = fc.c_current_cdemo_sk)
SELECT fc.c_customer_id AS customer_id, CONCAT(CONCAT(COALESCE(fc.c_last_name, ''), ', '), COALESCE(fc.c_first_name, '')) AS customername FROM filtered_customer AS fc JOIN filtered_demographics AS fd ON fc.c_current_cdemo_sk = fd.cd_demo_sk JOIN store_returns AS sr ON fd.cd_demo_sk = sr.sr_cdemo_sk ORDER BY fc.c_customer_id NULLS FIRST LIMIT 100