WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), demographic_filters AS (SELECT 'M' AS marital_status, '4 yr Degree' AS education_status, 100.00 AS min_price, 150.00 AS max_price UNION ALL SELECT 'S', 'Secondary', 50.00, 100.00 UNION ALL SELECT 'W', 'Advanced Degree', 150.00, 200.00), address_filters AS (SELECT 'FL,TX,DE' AS states, 100 AS min_profit, 200 AS max_profit UNION ALL SELECT 'IN,ND,ID', 150, 300 UNION ALL SELECT 'MT,IL,OH', 50, 250), filtered_sales AS (SELECT r_reason_desc, ws_quantity, wr_refunded_cash, wr_fee FROM web_sales JOIN web_returns ON ws_item_sk = wr_item_sk AND ws_order_number = wr_order_number JOIN filtered_dates ON ws_sold_date_sk = d_date_sk JOIN web_page ON ws_web_page_sk = wp_web_page_sk JOIN customer_demographics AS cd1 ON cd1.cd_demo_sk = wr_refunded_cdemo_sk JOIN customer_demographics AS cd2 ON cd2.cd_demo_sk = wr_returning_cdemo_sk JOIN customer_address ON ca_address_sk = wr_refunded_addr_sk JOIN reason ON r_reason_sk = wr_reason_sk JOIN demographic_filters AS df ON cd1.cd_marital_status = df.marital_status AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = df.education_status AND cd1.cd_education_status = cd2.cd_education_status AND ws_sales_price BETWEEN df.min_price AND df.max_price JOIN address_filters AS af ON ca_country = 'United States' AND (ca_state IN (SELECT SPLIT_PART(af.states, ',', 1) UNION ALL SELECT SPLIT_PART(af.states, ',', 2) UNION ALL SELECT SPLIT_PART(af.states, ',', 3)) AND ws_net_profit BETWEEN af.min_profit AND af.max_profit))
SELECT SUBSTRING(r_reason_desc, 1, 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) FROM filtered_sales GROUP BY r_reason_desc ORDER BY SUBSTRING(r_reason_desc, 1, 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) LIMIT 100