WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), filtered_household_demographics AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%'), customer_demographics_branch1 AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE cd_marital_status = 'M' AND cd_education_status = 'Unknown'), customer_demographics_branch2 AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree'), branch1 AS (SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM call_center JOIN catalog_returns ON cr_call_center_sk = cc_call_center_sk JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics_branch1 ON cd_demo_sk = c_current_cdemo_sk JOIN filtered_household_demographics ON hd_demo_sk = c_current_hdemo_sk JOIN filtered_customer_address ON ca_address_sk = c_current_addr_sk GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status), branch2 AS (SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM call_center JOIN catalog_returns ON cr_call_center_sk = cc_call_center_sk JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics_branch2 ON cd_demo_sk = c_current_cdemo_sk JOIN filtered_household_demographics ON hd_demo_sk = c_current_hdemo_sk JOIN filtered_customer_address ON ca_address_sk = c_current_addr_sk GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status)
SELECT Call_Center, Call_Center_Name, Manager, Returns_Loss FROM (SELECT * FROM branch1 UNION ALL SELECT * FROM branch2) AS union_result ORDER BY Returns_Loss DESC