WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), customer_filtered_address AS (SELECT ca_address_sk, ca_gmt_offset FROM customer_address WHERE ca_gmt_offset = -7), customer_filtered_household AS (SELECT hd_demo_sk, hd_buy_potential FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%'), branch1_returns AS (SELECT cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status, cr_net_loss FROM catalog_returns JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_filtered_address ON c_current_addr_sk = ca_address_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk AND cd_marital_status = 'M' AND cd_education_status = 'Unknown' JOIN customer_filtered_household ON hd_demo_sk = c_current_hdemo_sk JOIN call_center ON cr_call_center_sk = cc_call_center_sk), branch2_returns AS (SELECT cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status, cr_net_loss FROM catalog_returns JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_filtered_address ON c_current_addr_sk = ca_address_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk AND cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree' JOIN customer_filtered_household ON hd_demo_sk = c_current_hdemo_sk JOIN call_center ON cr_call_center_sk = cc_call_center_sk)
SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM (SELECT * FROM branch1_returns UNION ALL SELECT * FROM branch2_returns) AS all_returns GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status ORDER BY SUM(cr_net_loss) DESC