WITH date_range AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), item_filter AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 350), item_avg_discount AS (SELECT ws_item_sk, AVG(ws_ext_discount_amt) AS avg_discount FROM web_sales JOIN date_range ON ws_sold_date_sk = d_date_sk WHERE ws_item_sk IN (SELECT i_item_sk FROM item_filter) GROUP BY ws_item_sk)
SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales JOIN date_range ON ws_sold_date_sk = d_date_sk JOIN item_filter ON ws_item_sk = i_item_sk JOIN item_avg_discount AS iad ON web_sales.ws_item_sk = iad.ws_item_sk WHERE ws_ext_discount_amt > 1.3 * iad.avg_discount ORDER BY SUM(ws_ext_discount_amt) LIMIT 100