WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-02-01' AND CAST('1999-04-02' AS DATE)), filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'IL'), filtered_web AS (SELECT web_site_sk FROM web_site WHERE web_company_name = 'pri'), multi_warehouse_orders AS (SELECT ws_order_number FROM web_sales GROUP BY ws_order_number HAVING COUNT(DISTINCT ws_warehouse_sk) > 1), orders_without_returns AS (SELECT ws_order_number FROM (SELECT DISTINCT ws_order_number FROM web_sales) AS all_orders WHERE NOT ws_order_number IN (SELECT DISTINCT wr_order_number FROM web_returns))
SELECT COUNT(DISTINCT ws1.ws_order_number) AS "order count", SUM(ws1.ws_ext_ship_cost) AS "total shipping cost", SUM(ws1.ws_net_profit) AS "total net profit" FROM web_sales AS ws1 JOIN filtered_date AS fd ON ws1.ws_ship_date_sk = fd.d_date_sk JOIN filtered_ca AS fca ON ws1.ws_ship_addr_sk = fca.ca_address_sk JOIN filtered_web AS fw ON ws1.ws_web_site_sk = fw.web_site_sk JOIN multi_warehouse_orders AS mwo ON ws1.ws_order_number = mwo.ws_order_number JOIN orders_without_returns AS owr ON ws1.ws_order_number = owr.ws_order_number ORDER BY COUNT(DISTINCT ws1.ws_order_number) LIMIT 100