WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-02-01' AND CAST('1999-04-02' AS DATE)), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'IL'), filtered_website AS (SELECT web_site_sk FROM web_site WHERE web_company_name = 'pri'), multi_warehouse_orders AS (SELECT ws_order_number FROM web_sales GROUP BY ws_order_number HAVING COUNT(DISTINCT ws_warehouse_sk) > 1), returned_orders AS (SELECT DISTINCT wr_order_number FROM web_returns), filtered_web_sales AS (SELECT ws1.ws_order_number, ws1.ws_ext_ship_cost, ws1.ws_net_profit FROM web_sales AS ws1 JOIN filtered_dates ON ws1.ws_ship_date_sk = filtered_dates.d_date_sk JOIN filtered_address ON ws1.ws_ship_addr_sk = filtered_address.ca_address_sk JOIN filtered_website ON ws1.ws_web_site_sk = filtered_website.web_site_sk)
SELECT COUNT(DISTINCT fws.ws_order_number) AS "order count", SUM(fws.ws_ext_ship_cost) AS "total shipping cost", SUM(fws.ws_net_profit) AS "total net profit" FROM filtered_web_sales AS fws JOIN multi_warehouse_orders AS mwo ON fws.ws_order_number = mwo.ws_order_number LEFT JOIN returned_orders AS ro ON fws.ws_order_number = ro.wr_order_number WHERE ro.wr_order_number IS NULL ORDER BY COUNT(DISTINCT fws.ws_order_number) LIMIT 100