WITH wscs AS (SELECT sold_date_sk, sales_price FROM (SELECT ws_sold_date_sk AS sold_date_sk, ws_ext_sales_price AS sales_price FROM web_sales UNION ALL SELECT cs_sold_date_sk AS sold_date_sk, cs_ext_sales_price AS sales_price FROM catalog_sales)), wswscs_both_years AS (SELECT d.d_week_seq, d.d_year, SUM(CASE WHEN (d.d_day_name = 'Sunday') THEN w.sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN (d.d_day_name = 'Monday') THEN w.sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN (d.d_day_name = 'Tuesday') THEN w.sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN (d.d_day_name = 'Wednesday') THEN w.sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN (d.d_day_name = 'Thursday') THEN w.sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN (d.d_day_name = 'Friday') THEN w.sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN (d.d_day_name = 'Saturday') THEN w.sales_price ELSE NULL END) AS sat_sales FROM wscs AS w, date_dim AS d WHERE d.d_date_sk = w.sold_date_sk AND d.d_year IN (1998, 1999) GROUP BY d.d_week_seq, d.d_year), wswscs AS (SELECT d_week_seq, SUM(CASE WHEN (d_day_name = 'Sunday') THEN sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN (d_day_name = 'Monday') THEN sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN (d_day_name = 'Tuesday') THEN sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN (d_day_name = 'Wednesday') THEN sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN (d_day_name = 'Thursday') THEN sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN (d_day_name = 'Friday') THEN sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN (d_day_name = 'Saturday') THEN sales_price ELSE NULL END) AS sat_sales FROM wscs, date_dim WHERE d_date_sk = sold_date_sk GROUP BY d_week_seq)
SELECT y.d_week_seq AS d_week_seq1, ROUND(y.sun_sales / NULLIF(z.sun_sales, 0), 2), ROUND(y.mon_sales / NULLIF(z.mon_sales, 0), 2), ROUND(y.tue_sales / NULLIF(z.tue_sales, 0), 2), ROUND(y.wed_sales / NULLIF(z.wed_sales, 0), 2), ROUND(y.thu_sales / NULLIF(z.thu_sales, 0), 2), ROUND(y.fri_sales / NULLIF(z.fri_sales, 0), 2), ROUND(y.sat_sales / NULLIF(z.sat_sales, 0), 2) FROM (SELECT * FROM wswscs_both_years WHERE d_year = 1998) AS y JOIN (SELECT * FROM wswscs_both_years WHERE d_year = 1999) AS z ON y.d_week_seq = z.d_week_seq - 53 ORDER BY y.d_week_seq