WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_item AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 62 AND 62 + 30 AND i_manufact_id IN (129, 270, 821, 423)), filtered_inventory AS (SELECT inv_item_sk FROM inventory AS inv JOIN filtered_date AS fd ON inv.inv_date_sk = fd.d_date_sk WHERE inv_quantity_on_hand BETWEEN 100 AND 500), items_with_inventory AS (SELECT DISTINCT fi.i_item_sk, fi.i_item_id, fi.i_item_desc, fi.i_current_price FROM filtered_item AS fi JOIN filtered_inventory AS finv ON fi.i_item_sk = finv.inv_item_sk)
SELECT i_item_id, i_item_desc, i_current_price FROM items_with_inventory AS iwi WHERE EXISTS(SELECT 1 FROM store_sales AS ss WHERE ss.ss_item_sk = iwi.i_item_sk) ORDER BY i_item_id LIMIT 100