WITH filtered_dates AS (SELECT d_date_sk, d_week_seq, d_date
  FROM date_dim
  WHERE d_year = 1998
)
SELECT i_item_desc, w_warehouse_name, fd1.d_week_seq, SUM(CASE WHEN p_promo_sk IS NULL THEN 1 ELSE 0 END) AS no_promo, SUM(CASE WHEN NOT p_promo_sk IS NULL THEN 1 ELSE 0 END) AS promo, COUNT(*) AS total_cnt FROM catalog_sales JOIN inventory ON (cs_item_sk = inv_item_sk) JOIN warehouse ON (w_warehouse_sk = inv_warehouse_sk) JOIN item ON (i_item_sk = cs_item_sk) JOIN customer_demographics ON (cs_bill_cdemo_sk = cd_demo_sk) JOIN household_demographics ON (cs_bill_hdemo_sk = hd_demo_sk) JOIN filtered_dates AS fd1 ON (cs_sold_date_sk = fd1.d_date_sk) JOIN date_dim AS d2 ON (inv_date_sk = d2.d_date_sk) JOIN date_dim AS d3 ON (cs_ship_date_sk = d3.d_date_sk) LEFT OUTER JOIN promotion ON (cs_promo_sk = p_promo_sk) LEFT OUTER JOIN catalog_returns ON (cr_item_sk = cs_item_sk AND cr_order_number = cs_order_number) WHERE fd1.d_week_seq = d2.d_week_seq AND inv_quantity_on_hand < cs_quantity AND d3.d_date > fd1.d_date + INTERVAL '3 DAY' AND hd_buy_potential = '501-1000' AND cd_marital_status = 'M' AND cd_dep_count BETWEEN 9 AND 11 AND i_category IN ('Home', 'Men', 'Music') AND cs_wholesale_cost BETWEEN 34 AND 54 GROUP BY i_item_desc, w_warehouse_name, fd1.d_week_seq ORDER BY total_cnt DESC, i_item_desc, w_warehouse_name, fd1.d_week_seq LIMIT 100