WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1998-03-13' AND CAST('1998-03-13' AS DATE) + INTERVAL '90 DAY')
SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales, item, date_dim WHERE (i_manufact_id BETWEEN 341 AND 540 OR i_category IN ('Home', 'Men', 'Music')) AND i_item_sk = ws_item_sk AND d_date BETWEEN '1998-03-13' AND CAST('1998-03-13' AS DATE) + INTERVAL '90 DAY' AND d_date_sk = ws_sold_date_sk AND ws_wholesale_cost BETWEEN 26 AND 46 AND ws_ext_discount_amt > (SELECT 1.3 * AVG(ws_ext_discount_amt) FROM web_sales, date_dim WHERE ws_item_sk = i_item_sk AND d_date BETWEEN '1998-03-13' AND CAST('1998-03-13' AS DATE) + INTERVAL '90 DAY' AND d_date_sk = ws_sold_date_sk AND ws_wholesale_cost BETWEEN 26 AND 46 AND ws_sales_price / ws_list_price BETWEEN 34 * 0.01 AND 49 * 0.01) ORDER BY SUM(ws_ext_discount_amt) LIMIT 100