WITH filtered_dates_customers AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 12), date_range_anchor AS (SELECT d_month_seq FROM date_dim WHERE d_year = 1998 AND d_moy = 12 LIMIT 1), filtered_dates_revenue AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN (SELECT d_month_seq + 1 FROM date_range_anchor) AND (SELECT d_month_seq + 3 FROM date_range_anchor)), filtered_item AS (SELECT i_item_sk FROM item WHERE i_category = 'Women' AND i_class = 'maternity'), my_customers AS (SELECT DISTINCT c_customer_sk, c_current_addr_sk FROM (SELECT cs_sold_date_sk AS sold_date_sk, cs_bill_customer_sk AS customer_sk, cs_item_sk AS item_sk FROM catalog_sales UNION ALL SELECT ws_sold_date_sk AS sold_date_sk, ws_bill_customer_sk AS customer_sk, ws_item_sk AS item_sk FROM web_sales) AS cs_or_ws_sales, filtered_item, filtered_dates_customers, customer WHERE sold_date_sk = d_date_sk AND item_sk = i_item_sk AND c_customer_sk = cs_or_ws_sales.customer_sk), my_revenue AS (SELECT c_customer_sk, SUM(ss_ext_sales_price) AS revenue FROM my_customers, store_sales, customer_address, store, filtered_dates_revenue WHERE c_current_addr_sk = ca_address_sk AND ca_county = s_county AND ca_state = s_state AND ss_sold_date_sk = d_date_sk AND c_customer_sk = ss_customer_sk GROUP BY c_customer_sk), segments AS (SELECT CAST(ROUND(revenue / 50) AS INT) AS SEGMENT FROM my_revenue)
SELECT SEGMENT, COUNT(*) AS num_customers, SEGMENT * 50 AS segment_base FROM segments GROUP BY SEGMENT ORDER BY SEGMENT NULLS FIRST, num_customers NULLS FIRST, segment_base LIMIT 100