WITH filtered_date_dim AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN (CAST('1999-04-19' AS DATE) - INTERVAL '30 DAY') AND (CAST('1999-04-19' AS DATE) + INTERVAL '30 DAY'))
SELECT w_state, i_item_id, SUM(CASE WHEN (CAST(d_date AS DATE) < CAST('1999-04-19' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(d_date AS DATE) >= CAST('1999-04-19' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM catalog_sales LEFT OUTER JOIN catalog_returns ON (cs_order_number = cr_order_number AND cs_item_sk = cr_item_sk), warehouse, item, date_dim WHERE i_item_sk = cs_item_sk AND cs_warehouse_sk = w_warehouse_sk AND cs_sold_date_sk = d_date_sk AND d_date BETWEEN (CAST('1999-04-19' AS DATE) - INTERVAL '30 DAY') AND (CAST('1999-04-19' AS DATE) + INTERVAL '30 DAY') AND i_category = 'Home' AND i_manager_id BETWEEN 25 AND 64 AND cs_wholesale_cost BETWEEN 17 AND 36 AND cr_reason_sk = 16 GROUP BY w_state, i_item_id ORDER BY w_state, i_item_id LIMIT 100