WITH node_id AS (main_query), sql AS (SELECT item1.i_item_sk, item2.i_item_sk, COUNT(*) AS cnt FROM (SELECT ss_ticket_number, ss_item_sk FROM store_sales AS ss JOIN date_dim AS d ON ss.ss_sold_date_sk = d.d_date_sk WHERE d.d_year BETWEEN 2000 AND 2000 + 1 AND ss.ss_list_price BETWEEN 16 AND 30 AND EXISTS(SELECT 1 FROM customer AS c JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk WHERE ss.ss_customer_sk = c.c_customer_sk AND cd.cd_marital_status = 'S' AND cd.cd_education_status = 'Secondary')) AS s1 JOIN (SELECT ss_ticket_number, ss_item_sk FROM store_sales AS ss WHERE ss.ss_list_price BETWEEN 16 AND 30) AS s2 ON s1.ss_ticket_number = s2.ss_ticket_number JOIN item AS item1 ON s1.ss_item_sk = item1.i_item_sk JOIN item AS item2 ON s2.ss_item_sk = item2.i_item_sk WHERE item1.i_item_sk < item2.i_item_sk AND item1.i_category IN ('Electronics', 'Men') AND item2.i_manager_id BETWEEN 81 AND 100 GROUP BY item1.i_item_sk, item2.i_item_sk ORDER BY cnt)
SELECT item1.i_item_sk, item2.i_item_sk, COUNT(*) AS cnt FROM item AS item1, item AS item2, store_sales AS s1, store_sales AS s2, date_dim, customer, customer_address, customer_demographics WHERE item1.i_item_sk < item2.i_item_sk AND s1.ss_ticket_number = s2.ss_ticket_number AND s1.ss_item_sk = item1.i_item_sk AND s2.ss_item_sk = item2.i_item_sk AND s1.ss_customer_sk = c_customer_sk AND c_current_addr_sk = ca_address_sk AND c_current_cdemo_sk = cd_demo_sk AND d_year BETWEEN 2000 AND 2000 + 1 AND d_date_sk = s1.ss_sold_date_sk AND item1.i_category IN ('Electronics', 'Men') AND item2.i_manager_id BETWEEN 81 AND 100 AND cd_marital_status = 'S' AND cd_education_status = 'Secondary' AND s1.ss_list_price BETWEEN 16 AND 30 AND s2.ss_list_price BETWEEN 16 AND 30 GROUP BY item1.i_item_sk, item2.i_item_sk ORDER BY cnt