WITH date_dim_cte AS (SELECT d_date_sk FROM date_dim WHERE d_year BETWEEN 2000 AND 2000 + 1)
SELECT MIN(item1.i_item_sk), MIN(item2.i_item_sk), MIN(s1.ss_ticket_number), MIN(s1.ss_item_sk) FROM item AS item1, item AS item2, store_sales AS s1, store_sales AS s2, date_dim, customer, customer_address, customer_demographics WHERE item1.i_item_sk < item2.i_item_sk AND s1.ss_ticket_number = s2.ss_ticket_number AND s1.ss_item_sk = item1.i_item_sk AND s2.ss_item_sk = item2.i_item_sk AND s1.ss_customer_sk = c_customer_sk AND c_current_addr_sk = ca_address_sk AND c_current_cdemo_sk = cd_demo_sk AND d_year BETWEEN 2000 AND 2000 + 1 AND d_date_sk = s1.ss_sold_date_sk AND item1.i_category IN ('Electronics', 'Men') AND item2.i_manager_id BETWEEN 81 AND 100 AND cd_marital_status = 'S' AND cd_education_status = 'Secondary' AND s1.ss_list_price BETWEEN 16 AND 30 AND s2.ss_list_price BETWEEN 16 AND 30