WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-02-01' AND CAST('2002-04-02' AS DATE)), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'GA'), filtered_call_centers AS (SELECT cc_call_center_sk FROM call_center WHERE cc_county = 'Williamson County'), multi_warehouse_orders AS (SELECT cs_order_number FROM catalog_sales GROUP BY cs_order_number HAVING COUNT(DISTINCT cs_warehouse_sk) > 1), non_returned_orders AS (SELECT cr_order_number FROM catalog_returns)
SELECT COUNT(DISTINCT cs1.cs_order_number) AS "order count", SUM(cs1.cs_ext_ship_cost) AS "total shipping cost", SUM(cs1.cs_net_profit) AS "total net profit" FROM catalog_sales AS cs1 JOIN filtered_dates AS fd ON cs1.cs_ship_date_sk = fd.d_date_sk JOIN filtered_addresses AS fa ON cs1.cs_ship_addr_sk = fa.ca_address_sk JOIN filtered_call_centers AS fc ON cs1.cs_call_center_sk = fc.cc_call_center_sk JOIN multi_warehouse_orders AS mwo ON cs1.cs_order_number = mwo.cs_order_number LEFT JOIN non_returned_orders AS nro ON cs1.cs_order_number = nro.cr_order_number WHERE nro.cr_order_number IS NULL GROUP BY () ORDER BY COUNT(DISTINCT cs1.cs_order_number) LIMIT 100