WITH d1_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 4 AND d_year = 2001), d2_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 4 AND 10 AND d_year = 2001), d3_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 4 AND 10 AND d_year = 2001), filtered_store_sales AS (SELECT ss_net_profit, ss_item_sk, ss_store_sk, ss_customer_sk, ss_ticket_number FROM store_sales JOIN d1_filtered ON ss_sold_date_sk = d_date_sk), filtered_store_returns AS (SELECT sr_net_loss, sr_customer_sk, sr_item_sk, sr_ticket_number FROM store_returns JOIN d2_filtered ON sr_returned_date_sk = d_date_sk), filtered_catalog_sales AS (SELECT cs_net_profit, cs_bill_customer_sk, cs_item_sk FROM catalog_sales JOIN d3_filtered ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, i_item_desc, s_store_id, s_store_name, SUM(ss_net_profit) AS store_sales_profit, SUM(sr_net_loss) AS store_returns_loss, SUM(cs_net_profit) AS catalog_sales_profit FROM filtered_store_sales JOIN item ON i_item_sk = ss_item_sk JOIN store ON s_store_sk = ss_store_sk JOIN filtered_store_returns ON ss_customer_sk = sr_customer_sk AND ss_item_sk = sr_item_sk AND ss_ticket_number = sr_ticket_number JOIN filtered_catalog_sales ON sr_customer_sk = cs_bill_customer_sk AND sr_item_sk = cs_item_sk GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100