WITH filtered_store AS (SELECT s_store_sk FROM store WHERE s_county = 'Williamson County'), date_1_3 AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 3 AND d_year IN (1999, 2000, 2001)), date_25_28 AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 25 AND 28 AND d_year IN (1999, 2000, 2001)), hd_10000 AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential = '>10000' AND hd_vehicle_count > 0 AND (hd_dep_count * 1.000) / hd_vehicle_count > 1.2), hd_unknown AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential = 'Unknown' AND hd_vehicle_count > 0 AND (hd_dep_count * 1.000) / hd_vehicle_count > 1.2), union_branches AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN date_1_3 AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN hd_10000 AS h ON ss.ss_hdemo_sk = h.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN date_1_3 AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN hd_unknown AS h ON ss.ss_hdemo_sk = h.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN date_25_28 AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN hd_10000 AS h ON ss.ss_hdemo_sk = h.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN date_25_28 AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN hd_unknown AS h ON ss.ss_hdemo_sk = h.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk)
SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM (SELECT ss_ticket_number, ss_customer_sk, SUM(cnt) AS cnt FROM union_branches GROUP BY ss_ticket_number, ss_customer_sk HAVING SUM(cnt) BETWEEN 15 AND 20) AS dn JOIN customer AS c ON dn.ss_customer_sk = c.c_customer_sk ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, c_salutation NULLS FIRST, c_preferred_cust_flag DESC NULLS FIRST, ss_ticket_number NULLS FIRST LIMIT 100