WITH filtered_store AS (SELECT s_store_sk FROM store WHERE s_county = 'Williamson County'), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE (hd_buy_potential = '>10000' OR hd_buy_potential = 'Unknown') AND hd_vehicle_count > 0 AND (CASE WHEN hd_vehicle_count > 0 THEN (hd_dep_count * 1.000) / hd_vehicle_count ELSE NULL END) > 1.2), date_dom_1_3 AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001) AND d_dom BETWEEN 1 AND 3), date_dom_25_28 AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001) AND d_dom BETWEEN 25 AND 28), sales_dom_1_3 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN date_dom_1_3 ON store_sales.ss_sold_date_sk = date_dom_1_3.d_date_sk JOIN filtered_store ON store_sales.ss_store_sk = filtered_store.s_store_sk JOIN filtered_hd ON store_sales.ss_hdemo_sk = filtered_hd.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk HAVING COUNT(*) BETWEEN 15 AND 20), sales_dom_25_28 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN date_dom_25_28 ON store_sales.ss_sold_date_sk = date_dom_25_28.d_date_sk JOIN filtered_store ON store_sales.ss_store_sk = filtered_store.s_store_sk JOIN filtered_hd ON store_sales.ss_hdemo_sk = filtered_hd.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk HAVING COUNT(*) BETWEEN 15 AND 20), combined_sales AS (SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_dom_1_3 UNION ALL SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_dom_25_28)
SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM combined_sales JOIN customer ON ss_customer_sk = c_customer_sk ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, c_salutation NULLS FIRST, c_preferred_cust_flag DESC NULLS FIRST, ss_ticket_number NULLS FIRST