WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-02-01' AS DATE) AND CAST('2000-04-01' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 68 AND 98 AND i_manufact_id IN (677, 940, 694, 808)), inventory_with_dates AS (SELECT inv_item_sk FROM inventory JOIN filtered_dates ON inv_date_sk = d_date_sk WHERE inv_quantity_on_hand BETWEEN 100 AND 500), qualified_items AS (SELECT i_item_id, i_item_desc, i_current_price FROM filtered_items WHERE i_item_sk IN (SELECT inv_item_sk FROM inventory_with_dates) AND i_item_sk IN (SELECT cs_item_sk FROM catalog_sales))
SELECT i_item_id, i_item_desc, i_current_price FROM qualified_items ORDER BY i_item_id LIMIT 100