WITH filtered_date AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_moy = 11 AND d_year = 2000), filtered_item AS (SELECT i_item_sk, i_category_id, i_category FROM item WHERE i_manager_id = 1)
SELECT fd.d_year, fi.i_category_id, fi.i_category, SUM(ss.ss_ext_sales_price) FROM store_sales AS ss JOIN filtered_date AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_item AS fi ON ss.ss_item_sk = fi.i_item_sk GROUP BY fd.d_year, fi.i_category_id, fi.i_category ORDER BY SUM(ss.ss_ext_sales_price) DESC, fd.d_year, fi.i_category_id, fi.i_category LIMIT 100