WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price, i_manufact_id FROM item WHERE i_current_price BETWEEN 62 AND 92 AND i_manufact_id IN (129, 270, 821, 423)), filtered_inventory AS (SELECT inv_item_sk, inv_date_sk FROM inventory WHERE inv_quantity_on_hand BETWEEN 100 AND 500), qualified_inventory AS (SELECT inv_item_sk FROM filtered_inventory AS inv JOIN filtered_dates AS dd ON inv.inv_date_sk = dd.d_date_sk)
SELECT fi.i_item_id, fi.i_item_desc, fi.i_current_price FROM filtered_items AS fi JOIN qualified_inventory AS qi ON fi.i_item_sk = qi.inv_item_sk WHERE EXISTS(SELECT 1 FROM store_sales AS ss WHERE ss.ss_item_sk = fi.i_item_sk) ORDER BY fi.i_item_id LIMIT 100