WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), filtered_store_sales AS (SELECT ss_store_sk, ss_hdemo_sk, ss_cdemo_sk, ss_addr_sk, ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost, ss_sales_price, ss_net_profit FROM store_sales JOIN filtered_dates ON store_sales.ss_sold_date_sk = filtered_dates.d_date_sk)
SELECT AVG(ss_quantity) AS avg1, AVG(ss_ext_sales_price) AS avg2, AVG(ss_ext_wholesale_cost) AS avg3, SUM(ss_ext_wholesale_cost) FROM filtered_store_sales AS fss, store, customer_demographics, household_demographics, customer_address WHERE s_store_sk = fss.ss_store_sk AND ((fss.ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = fss.ss_cdemo_sk AND cd_marital_status = 'M' AND cd_education_status = 'Advanced Degree' AND fss.ss_sales_price BETWEEN 100.00 AND 150.00 AND hd_dep_count = 3) OR (fss.ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = fss.ss_cdemo_sk AND cd_marital_status = 'S' AND cd_education_status = 'College' AND fss.ss_sales_price BETWEEN 50.00 AND 100.00 AND hd_dep_count = 1) OR (fss.ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = fss.ss_cdemo_sk AND cd_marital_status = 'W' AND cd_education_status = '2 yr Degree' AND fss.ss_sales_price BETWEEN 150.00 AND 200.00 AND hd_dep_count = 1)) AND ((fss.ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('TX', 'OH', 'TX') AND fss.ss_net_profit BETWEEN 100 AND 200) OR (fss.ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('OR', 'NM', 'KY') AND fss.ss_net_profit BETWEEN 150 AND 300) OR (fss.ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('VA', 'TX', 'MS') AND fss.ss_net_profit BETWEEN 50 AND 250))