WITH date_three_years AS (SELECT d_date_sk FROM date_dim WHERE d_year BETWEEN 1999 AND 1999 + 2), date_nov_2001 AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 + 2 AND d_moy = 11), avg_sales AS (SELECT AVG(quantity * list_price) AS average_sales FROM (SELECT ss_quantity AS quantity, ss_list_price AS list_price FROM store_sales JOIN date_three_years ON ss_sold_date_sk = d_date_sk UNION ALL SELECT cs_quantity AS quantity, cs_list_price AS list_price FROM catalog_sales JOIN date_three_years ON cs_sold_date_sk = d_date_sk UNION ALL SELECT ws_quantity AS quantity, ws_list_price AS list_price FROM web_sales JOIN date_three_years ON ws_sold_date_sk = d_date_sk) AS sq2), cross_items_improved AS (SELECT i_item_sk AS ss_item_sk FROM item JOIN (SELECT DISTINCT i_brand_id AS brand_id, i_class_id AS class_id, i_category_id AS category_id FROM store_sales JOIN item AS iss ON ss_item_sk = iss.i_item_sk JOIN date_three_years AS d1 ON ss_sold_date_sk = d1.d_date_sk INTERSECT SELECT DISTINCT i_brand_id, i_class_id, i_category_id FROM catalog_sales JOIN item AS ics ON cs_item_sk = ics.i_item_sk JOIN date_three_years AS d2 ON cs_sold_date_sk = d2.d_date_sk INTERSECT SELECT DISTINCT i_brand_id, i_class_id, i_category_id FROM web_sales JOIN item AS iws ON ws_item_sk = iws.i_item_sk JOIN date_three_years AS d3 ON ws_sold_date_sk = d3.d_date_sk) AS sq1 ON i_brand_id = brand_id AND i_class_id = class_id AND i_category_id = category_id), cross_items AS (SELECT i_item_sk AS ss_item_sk FROM item, (SELECT iss.i_brand_id AS brand_id, iss.i_class_id AS class_id, iss.i_category_id AS category_id FROM store_sales, item AS iss, date_dim AS d1 WHERE ss_item_sk = iss.i_item_sk AND ss_sold_date_sk = d1.d_date_sk AND d1.d_year BETWEEN 1999 AND 1999 + 2 INTERSECT SELECT ics.i_brand_id, ics.i_class_id, ics.i_category_id FROM catalog_sales, item AS ics, date_dim AS d2 WHERE cs_item_sk = ics.i_item_sk AND cs_sold_date_sk = d2.d_date_sk AND d2.d_year BETWEEN 1999 AND 1999 + 2 INTERSECT SELECT iws.i_brand_id, iws.i_class_id, iws.i_category_id FROM web_sales, item AS iws, date_dim AS d3 WHERE ws_item_sk = iws.i_item_sk AND ws_sold_date_sk = d3.d_date_sk AND d3.d_year BETWEEN 1999 AND 1999 + 2) AS sq1 WHERE i_brand_id = brand_id AND i_class_id = class_id AND i_category_id = category_id)
SELECT channel, i_brand_id, i_class_id, i_category_id, SUM(sales) AS sum_sales, SUM(number_sales) AS sum_number_sales FROM (SELECT 'store' AS channel, i.i_brand_id, i.i_class_id, i.i_category_id, SUM(ss_quantity * ss_list_price) AS sales, COUNT(*) AS number_sales FROM store_sales AS ss JOIN date_nov_2001 AS dn ON ss.ss_sold_date_sk = dn.d_date_sk JOIN item AS i ON ss.ss_item_sk = i.i_item_sk WHERE ss.ss_item_sk IN (SELECT ss_item_sk FROM cross_items_improved) GROUP BY i.i_brand_id, i.i_class_id, i.i_category_id HAVING SUM(ss_quantity * ss_list_price) > (SELECT average_sales FROM avg_sales) UNION ALL SELECT 'catalog' AS channel, i.i_brand_id, i.i_class_id, i.i_category_id, SUM(cs_quantity * cs_list_price) AS sales, COUNT(*) AS number_sales FROM catalog_sales AS cs JOIN date_nov_2001 AS dn ON cs.cs_sold_date_sk = dn.d_date_sk JOIN item AS i ON cs.cs_item_sk = i.i_item_sk WHERE cs.cs_item_sk IN (SELECT ss_item_sk FROM cross_items_improved) GROUP BY i.i_brand_id, i.i_class_id, i.i_category_id HAVING SUM(cs_quantity * cs_list_price) > (SELECT average_sales FROM avg_sales) UNION ALL SELECT 'web' AS channel, i.i_brand_id, i.i_class_id, i.i_category_id, SUM(ws_quantity * ws_list_price) AS sales, COUNT(*) AS number_sales FROM web_sales AS ws JOIN date_nov_2001 AS dn ON ws.ws_sold_date_sk = dn.d_date_sk JOIN item AS i ON ws.ws_item_sk = i.i_item_sk WHERE ws.ws_item_sk IN (SELECT ss_item_sk FROM cross_items_improved) GROUP BY i.i_brand_id, i.i_class_id, i.i_category_id HAVING SUM(ws_quantity * ws_list_price) > (SELECT average_sales FROM avg_sales)) AS y GROUP BY ROLLUP (channel, i_brand_id, i_class_id, i_category_id) ORDER BY channel NULLS FIRST, i_brand_id NULLS FIRST, i_class_id NULLS FIRST, i_category_id NULLS FIRST LIMIT 100