WITH nov_2001_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 11), three_year_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year BETWEEN 1999 AND 2001), materialized_cross_items AS (SELECT ss_item_sk FROM (SELECT i_item_sk AS ss_item_sk FROM item, (SELECT iss.i_brand_id AS brand_id, iss.i_class_id AS class_id, iss.i_category_id AS category_id FROM store_sales JOIN three_year_dates ON store_sales.ss_sold_date_sk = three_year_dates.d_date_sk JOIN item AS iss ON store_sales.ss_item_sk = iss.i_item_sk INTERSECT SELECT ics.i_brand_id, ics.i_class_id, ics.i_category_id FROM catalog_sales JOIN three_year_dates ON catalog_sales.cs_sold_date_sk = three_year_dates.d_date_sk JOIN item AS ics ON catalog_sales.cs_item_sk = ics.i_item_sk INTERSECT SELECT iws.i_brand_id, iws.i_class_id, iws.i_category_id FROM web_sales JOIN three_year_dates ON web_sales.ws_sold_date_sk = three_year_dates.d_date_sk JOIN item AS iws ON web_sales.ws_item_sk = iws.i_item_sk) AS sq1 WHERE i_brand_id = brand_id AND i_class_id = class_id AND i_category_id = category_id) AS items), filtered_store_sales AS (SELECT ss_item_sk, ss_quantity, ss_list_price FROM store_sales JOIN nov_2001_dates ON store_sales.ss_sold_date_sk = nov_2001_dates.d_date_sk WHERE ss_item_sk IN (SELECT ss_item_sk FROM materialized_cross_items)), filtered_catalog_sales AS (SELECT cs_item_sk, cs_quantity, cs_list_price FROM catalog_sales JOIN nov_2001_dates ON catalog_sales.cs_sold_date_sk = nov_2001_dates.d_date_sk WHERE cs_item_sk IN (SELECT ss_item_sk FROM materialized_cross_items)), filtered_web_sales AS (SELECT ws_item_sk, ws_quantity, ws_list_price FROM web_sales JOIN nov_2001_dates ON web_sales.ws_sold_date_sk = nov_2001_dates.d_date_sk WHERE ws_item_sk IN (SELECT ss_item_sk FROM materialized_cross_items)), avg_sales_optimized AS (SELECT AVG(quantity * list_price) AS average_sales FROM (SELECT ss_quantity AS quantity, ss_list_price AS list_price FROM store_sales JOIN three_year_dates ON store_sales.ss_sold_date_sk = three_year_dates.d_date_sk UNION ALL SELECT cs_quantity AS quantity, cs_list_price AS list_price FROM catalog_sales JOIN three_year_dates ON catalog_sales.cs_sold_date_sk = three_year_dates.d_date_sk UNION ALL SELECT ws_quantity AS quantity, ws_list_price AS list_price FROM web_sales JOIN three_year_dates ON web_sales.ws_sold_date_sk = three_year_dates.d_date_sk) AS sq2), cross_items AS (SELECT i_item_sk AS ss_item_sk FROM item, (SELECT iss.i_brand_id AS brand_id, iss.i_class_id AS class_id, iss.i_category_id AS category_id FROM store_sales, item AS iss, date_dim AS d1 WHERE ss_item_sk = iss.i_item_sk AND ss_sold_date_sk = d1.d_date_sk AND d1.d_year BETWEEN 1999 AND 1999 + 2 INTERSECT SELECT ics.i_brand_id, ics.i_class_id, ics.i_category_id FROM catalog_sales, item AS ics, date_dim AS d2 WHERE cs_item_sk = ics.i_item_sk AND cs_sold_date_sk = d2.d_date_sk AND d2.d_year BETWEEN 1999 AND 1999 + 2 INTERSECT SELECT iws.i_brand_id, iws.i_class_id, iws.i_category_id FROM web_sales, item AS iws, date_dim AS d3 WHERE ws_item_sk = iws.i_item_sk AND ws_sold_date_sk = d3.d_date_sk AND d3.d_year BETWEEN 1999 AND 1999 + 2) AS sq1 WHERE i_brand_id = brand_id AND i_class_id = class_id AND i_category_id = category_id), avg_sales AS (SELECT AVG(quantity * list_price) AS average_sales FROM (SELECT ss_quantity AS quantity, ss_list_price AS list_price FROM store_sales, date_dim WHERE ss_sold_date_sk = d_date_sk AND d_year BETWEEN 1999 AND 1999 + 2 UNION ALL SELECT cs_quantity AS quantity, cs_list_price AS list_price FROM catalog_sales, date_dim WHERE cs_sold_date_sk = d_date_sk AND d_year BETWEEN 1999 AND 1999 + 2 UNION ALL SELECT ws_quantity AS quantity, ws_list_price AS list_price FROM web_sales, date_dim WHERE ws_sold_date_sk = d_date_sk AND d_year BETWEEN 1999 AND 1999 + 2) AS sq2)
SELECT channel, i_brand_id, i_class_id, i_category_id, SUM(sales) AS sum_sales, SUM(number_sales) AS sum_number_sales FROM (SELECT 'store' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ss_quantity * ss_list_price) AS sales, COUNT(*) AS number_sales FROM filtered_store_sales, item WHERE ss_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ss_quantity * ss_list_price) > (SELECT average_sales FROM avg_sales_optimized) UNION ALL SELECT 'catalog' AS channel, i_brand_id, i_class_id, i_category_id, SUM(cs_quantity * cs_list_price) AS sales, COUNT(*) AS number_sales FROM filtered_catalog_sales, item WHERE cs_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(cs_quantity * cs_list_price) > (SELECT average_sales FROM avg_sales_optimized) UNION ALL SELECT 'web' AS channel, i_brand_id, i_class_id, i_category_id, SUM(ws_quantity * ws_list_price) AS sales, COUNT(*) AS number_sales FROM filtered_web_sales, item WHERE ws_item_sk = i_item_sk GROUP BY i_brand_id, i_class_id, i_category_id HAVING SUM(ws_quantity * ws_list_price) > (SELECT average_sales FROM avg_sales_optimized)) AS y GROUP BY ROLLUP (channel, i_brand_id, i_class_id, i_category_id) ORDER BY channel NULLS FIRST, i_brand_id NULLS FIRST, i_class_id NULLS FIRST, i_category_id NULLS FIRST LIMIT 100