WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 11 AND d_year = 1998), filtered_items AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_manager_id = 8), filtered_sales AS (SELECT ss_item_sk, ss_customer_sk, ss_store_sk, ss_ext_sales_price FROM store_sales JOIN filtered_dates ON store_sales.ss_sold_date_sk = filtered_dates.d_date_sk), filtered_store AS (SELECT s_store_sk, s_zip FROM store)
SELECT fi.i_brand_id AS brand_id, fi.i_brand AS brand, fi.i_manufact_id, fi.i_manufact, SUM(fs.ss_ext_sales_price) AS ext_price FROM filtered_sales AS fs JOIN filtered_items AS fi ON fs.ss_item_sk = fi.i_item_sk JOIN customer AS c ON fs.ss_customer_sk = c.c_customer_sk JOIN customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_store AS s ON fs.ss_store_sk = s.s_store_sk WHERE SUBSTRING(ca.ca_zip, 1, 5) <> SUBSTRING(s.s_zip, 1, 5) GROUP BY fi.i_brand, fi.i_brand_id, fi.i_manufact_id, fi.i_manufact ORDER BY ext_price DESC, fi.i_brand, fi.i_brand_id, fi.i_manufact_id, fi.i_manufact LIMIT 100