WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), filtered_cd1 AS (SELECT cd_demo_sk, cd_dep_count FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'Unknown'), filtered_customer AS (SELECT c_customer_sk, c_current_cdemo_sk, c_current_addr_sk, c_birth_year FROM customer WHERE c_birth_month IN (1, 6, 8, 9, 12, 2)), filtered_address AS (SELECT ca_address_sk, ca_country, ca_state, ca_county FROM customer_address WHERE ca_state IN ('MS', 'IN', 'ND', 'OK', 'NM', 'VA', 'MS')), qualified_sales AS (SELECT cs_quantity, cs_list_price, cs_coupon_amt, cs_sales_price, cs_net_profit, cs_item_sk, cs_bill_customer_sk FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk), qualified_sales_with_cd1 AS (SELECT qs.*, cd1.cd_dep_count FROM qualified_sales AS qs JOIN filtered_cd1 AS cd1 ON cs_bill_cdemo_sk = cd1.cd_demo_sk), qualified_sales_with_customer AS (SELECT qs.*, c.c_current_cdemo_sk, c.c_current_addr_sk, c.c_birth_year FROM qualified_sales_with_cd1 AS qs JOIN filtered_customer AS c ON qs.cs_bill_customer_sk = c.c_customer_sk), qualified_sales_with_address AS (SELECT qs.*, ca.ca_country, ca.ca_state, ca.ca_county FROM qualified_sales_with_customer AS qs JOIN filtered_address AS ca ON qs.c_current_addr_sk = ca.ca_address_sk), full_qualified_sales AS (SELECT qs.*, i.i_item_id FROM qualified_sales_with_address AS qs JOIN item AS i ON qs.cs_item_sk = i.i_item_sk)
SELECT i_item_id, ca_country, ca_state, ca_county, AVG(CAST(cs_quantity AS DECIMAL(12, 2))) AS agg1, AVG(CAST(cs_list_price AS DECIMAL(12, 2))) AS agg2, AVG(CAST(cs_coupon_amt AS DECIMAL(12, 2))) AS agg3, AVG(CAST(cs_sales_price AS DECIMAL(12, 2))) AS agg4, AVG(CAST(cs_net_profit AS DECIMAL(12, 2))) AS agg5, AVG(CAST(c_birth_year AS DECIMAL(12, 2))) AS agg6, AVG(CAST(cd_dep_count AS DECIMAL(12, 2))) AS agg7 FROM full_qualified_sales AS qs JOIN customer_demographics AS cd2 ON qs.c_current_cdemo_sk = cd2.cd_demo_sk GROUP BY ROLLUP (i_item_id, ca_country, ca_state, ca_county) ORDER BY ca_country NULLS FIRST, ca_state NULLS FIRST, ca_county NULLS FIRST, i_item_id NULLS FIRST LIMIT 100