WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 11 AND d_year = 1998), filtered_items AS (SELECT i_item_sk, i_brand_id, i_brand, i_manufact_id, i_manufact FROM item WHERE i_manager_id = 8), filtered_stores AS (SELECT s_store_sk, SUBSTRING(s_zip, 1, 5) AS s_zip_prefix FROM store), filtered_customer_address AS (SELECT ca_address_sk, SUBSTRING(ca_zip, 1, 5) AS ca_zip_prefix FROM customer_address), joined_customers AS (SELECT c_customer_sk, ca_address_sk, ca_zip_prefix FROM customer JOIN filtered_customer_address ON c_current_addr_sk = ca_address_sk), filtered_sales AS (SELECT ss_ext_sales_price, ss_item_sk, ss_customer_sk, ss_store_sk FROM store_sales JOIN filtered_dates ON d_date_sk = ss_sold_date_sk)
SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM filtered_sales JOIN filtered_items ON ss_item_sk = i_item_sk JOIN joined_customers ON ss_customer_sk = c_customer_sk JOIN filtered_stores ON ss_store_sk = s_store_sk WHERE ca_zip_prefix <> s_zip_prefix GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand, i_brand_id, i_manufact_id, i_manufact LIMIT 100