WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('1999-02-22' AS DATE) AND CAST('1999-03-24' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_category, i_class, i_current_price FROM item WHERE i_category IN ('Sports', 'Books', 'Home')), filtered_sales AS (SELECT cs_ext_sales_price, cs_item_sk FROM catalog_sales JOIN filtered_dates ON catalog_sales.cs_sold_date_sk = filtered_dates.d_date_sk)
SELECT i.i_item_id, i.i_item_desc, i.i_category, i.i_class, i.i_current_price, SUM(s.cs_ext_sales_price) AS itemrevenue, SUM(s.cs_ext_sales_price) * 100.0000 / SUM(SUM(s.cs_ext_sales_price)) OVER (PARTITION BY i.i_class) AS revenueratio FROM filtered_sales AS s JOIN filtered_items AS i ON s.cs_item_sk = i.i_item_sk GROUP BY i.i_item_id, i.i_item_desc, i.i_category, i.i_class, i.i_current_price ORDER BY i.i_category NULLS FIRST, i.i_class NULLS FIRST, i.i_item_id NULLS FIRST, i.i_item_desc NULLS FIRST, revenueratio NULLS FIRST LIMIT 100