WITH filtered_dates_d1 AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 4 AND d_year = 2001), filtered_dates_d2_d3 AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 4 AND 10 AND d_year = 2001), filtered_store_sales AS (SELECT ss_item_sk, ss_store_sk, ss_customer_sk, ss_ticket_number, ss_net_profit FROM store_sales JOIN filtered_dates_d1 ON ss_sold_date_sk = d_date_sk), filtered_store_returns AS (SELECT sr_item_sk, sr_customer_sk, sr_ticket_number, sr_net_loss FROM store_returns JOIN filtered_dates_d2_d3 ON sr_returned_date_sk = d_date_sk), filtered_catalog_sales AS (SELECT cs_item_sk, cs_bill_customer_sk, cs_net_profit FROM catalog_sales JOIN filtered_dates_d2_d3 ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, i_item_desc, s_store_id, s_store_name, SUM(ss_net_profit) AS store_sales_profit, SUM(sr_net_loss) AS store_returns_loss, SUM(cs_net_profit) AS catalog_sales_profit FROM filtered_store_sales JOIN filtered_store_returns ON (filtered_store_sales.ss_customer_sk = filtered_store_returns.sr_customer_sk AND filtered_store_sales.ss_item_sk = filtered_store_returns.sr_item_sk AND filtered_store_sales.ss_ticket_number = filtered_store_returns.sr_ticket_number) JOIN filtered_catalog_sales ON (filtered_store_returns.sr_customer_sk = filtered_catalog_sales.cs_bill_customer_sk AND filtered_store_returns.sr_item_sk = filtered_catalog_sales.cs_item_sk) JOIN item ON (filtered_store_sales.ss_item_sk = i_item_sk) JOIN store ON (filtered_store_sales.ss_store_sk = s_store_sk) GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100