WITH date_range AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), manufacturer_items AS (SELECT i_item_sk FROM item WHERE i_manufact_id = 977), catalog_sales_filtered AS (SELECT cs_item_sk, cs_ext_discount_amt FROM catalog_sales WHERE cs_sold_date_sk IN (SELECT d_date_sk FROM date_range)), item_avg_discount AS (SELECT cs_item_sk, 1.3 * AVG(cs_ext_discount_amt) AS avg_threshold FROM catalog_sales_filtered GROUP BY cs_item_sk)
SELECT SUM(cs.cs_ext_discount_amt) AS "excess discount amount" FROM catalog_sales_filtered AS cs JOIN manufacturer_items AS mi ON cs.cs_item_sk = mi.i_item_sk JOIN item_avg_discount AS avg ON cs.cs_item_sk = avg.cs_item_sk WHERE cs.cs_ext_discount_amt > avg.avg_threshold LIMIT 100