WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), item_avg_discount AS (SELECT cs_item_sk, 1.3 * AVG(cs_ext_discount_amt) AS threshold FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk GROUP BY cs_item_sk)
SELECT SUM(cs_ext_discount_amt) AS "excess discount amount" FROM catalog_sales JOIN item ON i_item_sk = cs_item_sk JOIN filtered_dates ON cs_sold_date_sk = d_date_sk JOIN item_avg_discount AS iad ON catalog_sales.cs_item_sk = iad.cs_item_sk WHERE i_manufact_id = 977 AND cs_ext_discount_amt > iad.threshold LIMIT 100