WITH filtered_date_dim AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 5), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), electronics_manufacturers AS (SELECT DISTINCT i_manufact_id, i_item_sk FROM item WHERE i_category IN ('Electronics')), ss AS (SELECT em.i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales JOIN filtered_date_dim AS fd ON ss_sold_date_sk = fd.d_date_sk JOIN filtered_customer_address AS fca ON ss_addr_sk = fca.ca_address_sk JOIN electronics_manufacturers AS em ON ss_item_sk = em.i_item_sk GROUP BY em.i_manufact_id), ws AS (SELECT em.i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales JOIN filtered_date_dim AS fd ON ws_sold_date_sk = fd.d_date_sk JOIN filtered_customer_address AS fca ON ws_bill_addr_sk = fca.ca_address_sk JOIN electronics_manufacturers AS em ON ws_item_sk = em.i_item_sk GROUP BY em.i_manufact_id), cs AS (SELECT em.i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales JOIN filtered_date_dim AS fd ON cs_sold_date_sk = fd.d_date_sk JOIN filtered_customer_address AS fca ON cs_bill_addr_sk = fca.ca_address_sk JOIN electronics_manufacturers AS em ON cs_item_sk = em.i_item_sk GROUP BY em.i_manufact_id)
SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales LIMIT 100